#!/usr/bin/python# -*- coding: utf-8 -*-import NameExtractorimport codecs    ###########Extraction of named entity for Discours-Sarkozy############fh = codecs.open("Discours-Sarkozy-v20111025.preproc.txt") text = fh.read()fh.close()dict_names = {}dict_names,nb = NameExtractor.name_extraction(text, dict_names)names = dict_names.keys()file = open("result_discours_Sarkozy.txt","w",0)file.write("Named entity for Discours Sarkozy \n\n\n")for n in names:    file.write(n+"\n")file.close()     